﻿using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.Events;

public class DelayedEvent : MonoBehaviour
{
    public List<TimedEvent> timedEvents;
    public bool stopQuedEventsIfTriggered;

    public void DoEventsDelayed()
    {
        if (stopQuedEventsIfTriggered)
            StopAllCoroutines();

        foreach (TimedEvent ev in timedEvents)
            StartCoroutine(DelayEvents(ev));
    }

    public void CancelEvents()
    {
        StopAllCoroutines();
    }

    IEnumerator DelayEvents(TimedEvent ev)
    {
        yield return new WaitForSeconds(ev.delay);
        ev.delayedEvent?.Invoke();
    }
}

[System.Serializable]
public struct TimedEvent
{
    public UnityEvent delayedEvent;
    public float delay;
}

﻿using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.XR.Interaction.Toolkit;

public class DisableSelectedObjectCollider : MonoBehaviour
{

    // TODO: currently if this script is placed on the left hand and right hands, the puzzle 2 projector interaction
    // will not function correctly. Most likely if we want to use this script we will have to make sure that we
    // are only doing this for grab interactables.

    // This script enables and disables the box collider on the object that was selected. This was needed because
    // There was physics issues when trying to move using locomation while holding an object.
    private void Awake() 
    {        
        gameObject.GetComponent<XRBaseInteractor>().selectEntered.AddListener(this.OnSelectEnter); 
        gameObject.GetComponent<XRBaseInteractor>().selectExited.AddListener(this.OnSelectExit); 
    }
    void OnSelectEnter(SelectEnterEventArgs args)
    {
        // still need the collider to for the XR to work but it should not participate in collisions.
        args.interactable.gameObject.GetComponentInChildren<Collider>().isTrigger = true;
    }

    void OnSelectExit(SelectExitEventArgs args)
    {
        // When we let go it should collide normally again.
        args.interactable.gameObject.GetComponentInChildren<Collider>().isTrigger = false;
    }
}

