using UnityEngine;

public class HandAnimationManager : MonoBehaviour
{
    [SerializeField] List<AnimationStateConfig> states;

    Animator anim;

    private void Start()
    {
        anim = GetComponent<Animator>();
    }
    public void SetState(int stateId)
    {
        if (stateId < states.Count)
        {
            if (states[stateId].boolState.name.Length > 0)
                anim.SetBool(states[stateId].boolState.name, states[stateId].boolState.state);
            if (states[stateId].trigger.Length > 0)
                anim.SetTrigger(states[stateId].trigger);
        }
    }
}

[System.Serializable]
public struct AnimationStateConfig
{
    public string trigger; // Leave name empty if not need use
    public AnimBoolState boolState; // Leave name empty if not need use
}

[System.Serializable]
public struct AnimBoolState
{
    public string name; // Leave name empty if not need use
    public bool state;
}

﻿using System.Collections;
