[RequireComponent(typeof(XRRayInteractor))]
public class RayToggler : MonoBehaviour
{

    // This script enables and disables the ray interactor so that you have to hold and release the trigger to 
    // teleport.

    // Reference to teleport activate action.
    [SerializeField] private InputActionReference activateReference;

    private XRRayInteractor rayInteractor = null;
    private bool isEnabled = false;

    private void Awake() 
    {
        rayInteractor = GetComponent<XRRayInteractor>();    
    }

    private void OnEnable()
    {
        activateReference.action.started += ToggleRay;
        activateReference.action.canceled += ToggleRay;
    }

    private void OnDisable() 
    {
        activateReference.action.started -= ToggleRay;
        activateReference.action.canceled -= ToggleRay;
    }

    private void ToggleRay(InputAction.CallbackContext context)
    {
        // Enable ray interactor when trigger is pressed otherwise it should be disabled.
        isEnabled = context.control.IsPressed();

    }

    private void LateUpdate() 
    {
        ApplyStatus();
    }

    private void ApplyStatus()
    {
        if(rayInteractor.enabled != isEnabled)
            rayInteractor.enabled = isEnabled;
    }
}

