﻿using Photon.Pun;
using System.Collections;
using System.Collections.Generic;
using UnityEngine;

[RequireComponent(typeof(Rigidbody))]
public class RigidbodyFollow : MonoBehaviour, IPunObservable
{
    Rigidbody rb;
    [SerializeField] Transform target;
    [SerializeField] bool usePunInterpolateSync;

    void Awake()
    {
        rb = GetComponent<Rigidbody>();
    }

    void FixedUpdate()
    {
        rb.MovePosition(target.position);
        rb.MoveRotation(target.rotation);
    }

    void IPunObservable.OnPhotonSerializeView(PhotonStream stream, PhotonMessageInfo info)
    {
        if (stream.IsWriting)
        {
            // Send current rotation
            stream.SendNext(transform.rotation);
        }
        else
        {
            // Receive and smooth out rotation
            Quaternion targetRotation = (Quaternion)stream.ReceiveNext();
            transform.rotation = Quaternion.Lerp(transform.rotation, targetRotation, Time.deltaTime * 5.0f);
        }
    }
}

