﻿using UnityEngine;
using System.Collections;
using UnityEngine.XR;
using UnityEngine.SceneManagement;
using UnityEngine.Events;

public class SplashController : MonoBehaviour {

	//public float minimumTimeToShowLogo = 5f;
	public string levelToLoad = "";
	public bool recenter = true;
	public bool loadOnStart = true;
	public bool waitToLoad = false;

	public AnimationClip anim;
	public float dur = 5f;

	public float fadeDur = 0f;
	public GameObject loadingBar;
	public GameObject fadeCanvas;
	public UnityEvent EventOnLoadStart;

	public bool waitStartAsync;

	AsyncOperation async;

	void Start()
    {
		Application.backgroundLoadingPriority = ThreadPriority.Normal;

		if (waitToLoad)
			canLoad = false;

		if (recenter)
			InputTracking.Recenter();

		StartCoroutine(DelayedActivate());
	}


	bool canLoad = false;
    public void LoadAsyncNow()
    {
		canLoad = true;
		waitStartAsync = false;
	}

    IEnumerator DelayedActivate()
	{
		if (!waitStartAsync)
		{
			async = SceneManager.LoadSceneAsync(levelToLoad); //	StartCoroutine(delayedThing());
			async.allowSceneActivation = false;
		}

		while (!canLoad && !loadOnStart)
			yield return null;

		if (fadeCanvas)
			fadeCanvas.SetActive(true);

		yield return new WaitForSeconds(fadeDur);

		if (async == null)
		{
			async = SceneManager.LoadSceneAsync(levelToLoad); //	StartCoroutine(delayedThing());
			async.allowSceneActivation = false;
		}

		if (loadingBar)
			loadingBar.SetActive(true);

		EventOnLoadStart?.Invoke();

		float d = 0;
		while (async.progress < 0.9f)
		{
			d += Time.deltaTime;
			yield return null;
		}

		yield return new WaitForSeconds(dur - d);

		async.allowSceneActivation = true;

		//Application.backgroundLoadingPriority = ThreadPriority.Low;
		//SceneManager.SetActiveScene(SceneManager.GetSceneByName(levelToLoad));
	}
}

﻿using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class TopScale : MonoBehaviour
{

    public void StartScale()
    {
        StartCoroutine("ScaleThickness");
    }

    IEnumerator ScaleThickness()
    {
        while (transform.localScale.x < 0.19f)
        {
            float newScale = Mathf.Lerp(transform.localScale.x, 0.2f, Time.deltaTime * 1.3f);
            transform.localScale = new Vector3(newScale, 0.55f, newScale);
            yield return null;
        }
    }
}

